package org.testing.devcode;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	         // factorial programe on 5*4*3*2=120
		      int  fact=1;
		      int num=5;
		      for(int i=1;i<=num;i++) {
		    	  fact=fact*i;
		      }
		      System.out.println("factorial is:"+num+" "+fact);
			}

	}


